import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import api from "../utils/api";

export default function Goals() {
  const [goals, setGoals] = useState([]);
  const [form, setForm] = useState({ goal_name:"", goal_amount:"", description:"", end_date:"" });

  const load = async () => {
    const res = await api.get("/goals");
    setGoals(res.data);
  };
  useEffect(()=>{ load(); },[]);

  const add = async (e) => {
    e.preventDefault();
    await api.post("/goals", { ...form, goal_amount: Number(form.goal_amount) });
    setForm({ goal_name:"", goal_amount:"", description:"", end_date:"" });
    load();
  };

  const del = async (id) => {
    try{ await api.delete(`/goals/${id}`); load(); }catch(e){ alert(e.response?.data?.error || "Cannot delete"); }
  };
  const edit = async (g) => {
    const name = prompt("Goal name", g.goal_name);
    if (!name) return;
    const amt = Number(prompt("Goal amount", g.goal_amount));
    const desc = prompt("Description", g.description || "") || "";
    const date = prompt("End date (YYYY-MM-DD)", g.end_date);
    try { await api.put(`/goals/${g.id}`, { goal_name: name, goal_amount: amt, description: desc, end_date: date }); load(); }
    catch(e){ alert(e.response?.data?.error || "Cannot edit"); }
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="card">
          <h2>Goals</h2>
          <form onSubmit={add} className="flex" style={{flexDirection:"column"}}>
            <input placeholder="Goal Name" value={form.goal_name} onChange={(e)=>setForm({...form, goal_name:e.target.value})} required/>
            <input type="number" placeholder="Amount to Save" value={form.goal_amount} onChange={(e)=>setForm({...form, goal_amount:e.target.value})} required/>
            <textarea placeholder="Description" value={form.description} onChange={(e)=>setForm({...form, description:e.target.value})} />
            <input type="date" value={form.end_date} onChange={(e)=>setForm({...form, end_date:e.target.value})} required/>
            <button type="submit">Add Goal</button>
          </form>
        </div>

        <div className="row" style={{marginTop:16}}>
          {goals.map(g => (
            <div key={g.id} className="card">
              <div className="flex" style={{justifyContent:"space-between"}}>
                <h3>{g.goal_name}</h3>
                <span className={`badge ${g.status}`}>{g.status}</span>
              </div>
              <p>Target: ‚Çπ {Number(g.goal_amount).toFixed(2)}</p>
              <p>Saved: ‚Çπ {Number(g.saved).toFixed(2)}</p>
              <Progress value={Math.min(100, Math.round((Number(g.saved)/Number(g.goal_amount))*100))} />
              <p>Remaining: ‚Çπ {Number(g.remaining).toFixed(2)}</p>
              <p>Ends on: {g.end_date}</p>
              {g.status==="achieved" ? <span className="badge">üèÜ Achieved</span> :
               g.status==="expired" ? <span>Sorry, goal not achieved. Remaining ‚Çπ {Number(g.remaining).toFixed(2)}</span> :
               (<div className="flex"><button onClick={()=>edit(g)}>Edit</button><button onClick={()=>del(g.id)}>Delete</button></div>)
              }
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

function Progress({ value }){
  return (
    <div style={{height:10, background:"#1f2937", borderRadius:999}}>
      <div style={{width: value+"%", height:10, borderRadius:999, background: "linear-gradient(90deg, #22c55e, #3b82f6)"}}></div>
    </div>
  );
}
