import React, { useEffect, useState } from "react";
import Navbar from "../components/Navbar";
import api from "../utils/api";

export default function ViewTransactions() {
  const [rows, setRows] = useState([]);
  const [sortBy, setSortBy] = useState("date");
  const [order, setOrder] = useState("desc");
  const [category, setCategory] = useState("");

  const load = async () => {
    const res = await api.get("/transactions", { params: { sortBy, order, category: category || undefined } });
    setRows(res.data);
  };
  useEffect(()=>{ load(); }, [sortBy, order, category]);

  const toggleSort = (col) => {
    if (sortBy === col) setOrder(order === "asc" ? "desc" : "asc");
    else { setSortBy(col); setOrder("asc"); }
  };

  const download = () => {
    window.open((process.env.REACT_APP_API_URL || "http://localhost:5000") + "/transactions/export", "_blank");
  };

  return (
    <div>
      <Navbar />
      <div className="container">
        <div className="card">
          <h2>Transactions</h2>
          <div className="flex">
            <select value={category} onChange={(e)=>setCategory(e.target.value)}>
              <option value="">All Categories</option>
              {["food","travel","bills","recharge","rent","loan","donations","savings","other"].map(c => <option key={c} value={c}>{c}</option>)}
            </select>
            <button onClick={download}>Download Reports</button>
          </div>
          <table>
            <thead>
              <tr>
                <th onClick={()=>toggleSort("amount")}>Amount {sortBy==="amount" ? (order==="asc"?"↑":"↓") : ""}</th>
                <th onClick={()=>toggleSort("category")}>Category {sortBy==="category" ? (order==="asc"?"↑":"↓") : ""}</th>
                <th onClick={()=>toggleSort("date")}>Date {sortBy==="date" ? (order==="asc"?"↑":"↓") : ""}</th>
                <th>Notes</th>
              </tr>
            </thead>
            <tbody>
              {rows.map(r => (
                <tr key={r.id}>
                  <td>₹ {Number(r.amount).toFixed(2)}</td>
                  <td>{r.category}{r.goal_name ? ` → ${r.goal_name}` : ""}</td>
                  <td>{new Date(r.date).toISOString().slice(0,10)}</td>
                  <td>{r.notes || ""}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
