// Dashboard.js
import React, { useEffect, useState } from "react";
import { PieChart, Pie, Cell, Tooltip, Legend, ResponsiveContainer } from "recharts";
import "../styles/Dashboard.css";

const Dashboard = () => {
  const [salary, setSalary] = useState(30000);
  const [expenses, setExpenses] = useState(3000);
  const [savings, setSavings] = useState(salary - expenses);

  const data = [
    { name: "food", value: 1000 },
    { name: "other", value: 2000 },
    { name: "savings", value: savings },
  ];

  const COLORS = ["#3b82f6", "#22c55e", "#facc15", "#ef4444"];

  useEffect(() => {
    setSavings(salary - expenses);
  }, [salary, expenses]);

  return (
    <div className="dashboard-container">
      <h2 className="dashboard-title">Welcome to BudgetMate</h2>
      <p className="dashboard-subtitle">
        The art is not in making money, but in keeping it.
      </p>

      {/* Top Cards */}
      <div className="dashboard-cards">
        <div className="card">
          <h3>Monthly Salary</h3>
          <p>₹ {salary.toFixed(2)}</p>
        </div>
        <div className="card">
          <h3>Total Expenses</h3>
          <p>₹ {expenses.toFixed(2)}</p>
        </div>
        <div className="card">
          <h3>Current Balance</h3>
          <p>₹ {savings.toFixed(2)}</p>
        </div>
      </div>

      {/* Chart Section */}
      <div className="chart-container">
        <h3>Expenses vs Savings</h3>
        <ResponsiveContainer width="100%" height={300}>
          <PieChart>
            <Pie
              data={data}
              cx="50%"
              cy="50%"
              innerRadius={70}
              outerRadius={120}
              paddingAngle={3}
              dataKey="value"
              labelLine={false}
              label={({ value }) => `₹${value}`} // only values shown inside
            >
              {data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip formatter={(value, name) => [`₹${value}`, name]} />
            <Legend verticalAlign="bottom" height={36} />
          </PieChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
};

export default Dashboard;
